        items.map(item=>{
            return(


                <div className="cart-item">
                    <div className="left-text">
                <button className="remove"
                onClick={()=>deleteItem(item.objectId)}>
                    <i className="fa fa-trash">
                        </i></button>

                <div className="item-img">
                <img src={item.ItemImage.url} alt={item.ItemName} />
