            dispatch({ 
           type: "REMOVE_ITEM", 
           payload:id
         });
       },
        removeItem: (id)=>{
            dispatch({ 
        type: "REMOVE_ITEM", 
        payload:id
        });
        },
        removeItem: (id)=>{
            dispatch({ 
        type: "REMOVE_ITEM", 
        payload:id
        });
        }
    }
}
export default connect(mapStateToProps,mapDispatchToProps)(Cart)
